    //try { // temp
    for {
        set message = ..GetMessage()
        quit:(message = "") // if client is gone, finish looping
        if (message.error '= "") {
            if (message.error '[ "ERROR #7951") { // don't try and send message if it was a WS close error
                set st = ..Send("error", message.error)
