do ##class(%Device).ReDirectIO($$$YES)

set tStatement = ##class(%SQL.Statement).%New()
set qStatus = tStatement.%Prepare(query)
if qStatus'=1 {
    write $System.Status.DisplayError(qStatus)
} else {
    set rset = tStatement.%Execute()
    do rset.%Display()
}

